import React, {useState} from 'react';

import Card from './Card';
import Button from './Button';
// import './AddUser.css';

const AddItem = (props) => {

  // only doing img and name for now since that's all that's required for this checkpoint - eventually we will add description and tags as well
  const [enteredImg, setEnteredImg] = useState('');
  const [enteredName, setEnteredName] = useState('');

  const imgChangeHandler = (event) => {
    setEnteredImg(event.target.value);
  };

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const userData = {
      img: enteredImg,
      name: enteredname
    }

    if (!(userData.id.trim().length === 0)) {
      props.onAddItem(userData);
    }

    setEnteredId('');
    setEnteredAge('');
    setEnteredImg('');
    setEnteredname('');
  }

  return (
    <Card className="input">
      <form onSubmit={submitHandler}>
        <label>Link to image</label>
         <input
          id="img"
          type="text"
          value = {enteredImg}
          onChange = {imgChangeHandler}
        />
        <label>name</label>
         <input
          id="name"
          type="text"
          value = {enteredName}
          onChange = {nameChangeHandler}
        />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddItem;
