import React, {useState} from 'react';

import Card from './Card';
import Button from './Button';
import './AddUser.css'

const AddUser = (props) => {

  // only doing img and name for now since that's all that's required for this checkpoint - eventually we will add description and tags as well
  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredUsername, setEnteredUsername] = useState('');
  const [enteredPassword, setEnteredPassword] = useState('');

  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);
  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const itemData = {
      img: enteredImg,
      name: enteredName,
      description: enteredDescription
    }

    if (!(itemData.name.trim().length === 0)) {
      props.onAddItem(itemData);
    }

    setEnteredEmail('');
    setEnteredUsername('');
    setEnteredPassword('');
  }

  return (
    <Card className="input">
      <form onSubmit={submitHandler}>
        <label>Email</label>
         <input
          id="email"
          type="text"
          value = {enteredEmail}
          onChange = {emailChangeHandler}
        />
        <label>Username</label>
         <input
          id="username"
          type="text"
          value = {enteredUsername}
          onChange = {usernameChangeHandler}
        />
        <label>Password</label>
         <input
          id="password"
          type="text"
          value = {enteredPassword}
          onChange = {passwordChangeHandler}
        />
        <Button type="submit">Pin Item</Button>
      </form>
    </Card>
  );
};

export default AddUser;
