import React from 'react';
import EditItem from './components/EditItem'
import { useNavigate } from "react-router-dom";
import Axios from 'axios';
import { models } from 'mongoose';
import { useParams } from 'react-router-dom';

async function axios_get(id) {
    let updatedData;
    await Axios.get(`http://localhost:8080/pins/${id}`)
    .then(response => {
      const {name, img, description} = response.data;
      const currData = {
        name: name,
        img: img,
        description: description
      }
    })
    .catch(error => console.log(error));
    return currData;
}

function EditItemPage(props) {
  const navigate = useNavigate();

  const { id } = useParams();

  const currData = axios_get(id);
  const currName = currData.name;
  const currImg = currData.img;
  const currDescription = currData.description;


    const itemEditHandler = async function(itemData) {
        await Axios.put(`http://localhost:8080/pins/${id}`, itemData)
        .then(response => {})
        .catch(error => console.log(error));
        navigate('/loggedin')
    };

  return (
    <div>
        <EditItem currName={currName} currImg={currImg} currDescription={currDescription} onEditItem={itemEditHandler} />
    </div>
  );
}

export default EditItemPage;
